import React, { useState } from 'react';
import axios from 'axios';

const ComponentsForm: React.FC = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [errorMessage, setErrorMessage] = useState<string | null>(null);

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/api/form', {
        name,
        email,
        phone,
      });
      console.log('Form submitted:', response.data);
      setErrorMessage(null); // скидаємо повідомлення про помилку, якщо все добре
    } catch (error) {
      console.error('Error submitting form:', error);
      setErrorMessage('Сталася помилка при відправленні форми.'); // встановлюємо повідомлення про помилку
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="tel"
          placeholder="Phone"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
          required
        />
        <button type="submit">Відправити</button>
      </form>

      {errorMessage && (
        <div className="error-popup">
          {errorMessage}
        </div>
      )}
    </div>
  );
};

export default ComponentsForm;
