// src/components/FormComponent.tsx
import React, { useState } from 'react';
import axios from 'axios';

const FormComponent: React.FC = () => {
    const [formId, setFormId] = useState<string>('');
    const [formData, setFormData] = useState<any>(null);
    const [submittedData, setSubmittedData] = useState<any>(null);

    const fetchForm = async () => {
        try {
            const response = await axios.get(`/api/jotform/${formId}`);
            setFormData(response.data.formData);
        } catch (error) {
            console.error('Error fetching form:', error);
        }
    };

    const handleSubmit = async (data: any) => {
        try {
            const response = await axios.post('/api/jotform/webhook', data);
            setSubmittedData(response.data);
        } catch (error) {
            console.error('Error submitting form:', error);
        }
    };

    return (
        <div>
            <input
                type="text"
                value={formId}
                onChange={(e) => setFormId(e.target.value)}
                placeholder="Enter JotForm ID"
            />
            <button onClick={fetchForm}>Fetch Form</button>

            {formData && (
                <form
                    onSubmit={(e) => {
                        e.preventDefault();
                        const data = new FormData(e.target as HTMLFormElement);
                        handleSubmit(Object.fromEntries(data));
                    }}
                >
                    {formData.fields.map((field: any) => (
                        <div key={field.id}>
                            <label>{field.label}</label>
                            <input type={field.type} name={field.id} required={field.required} />
                        </div>
                    ))}
                    <button type="submit">Submit</button>
                </form>
            )}

            {submittedData && <p>{submittedData.message}</p>}
        </div>
    );
};

export default FormComponent;
